import React from 'react';

const UploadProject = () => {
    const handleSubmit = (e) => {
        e.preventDefault();
        alert("Project submission is disabled in local demo mode.");
        // In a real app, you would collect form data and send a POST request
    };

    return (
        <div className="container mt-4">
            <h2>Upload a New Project</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Project Title</label>
                    <input type="text" className="form-control" id="title" required />
                </div>
                <div className="mb-3">
                    <label htmlFor="description" className="form-label">Description</label>
                    <textarea className="form-control" id="description" rows="3" required></textarea>
                </div>
                 <div className="mb-3">
                    <label htmlFor="tech" className="form-label">Technologies (comma-separated)</label>
                    <input type="text" className="form-control" id="tech" placeholder="e.g., Java, React, SQL" />
                </div>
                <div className="mb-3">
                    <label htmlFor="link" className="form-label">Code/Demo Link</label>
                    <input type="url" className="form-control" id="link" />
                </div>
                <button type="submit" className="btn btn-primary">Submit Project</button>
            </form>
        </div>
    );
};

export default UploadProject;
